DROP TABLE IF EXISTS `holiday_difinitions`;
CREATE TABLE `holiday_difinitions` (
  `id` int NOT NULL AUTO_INCREMENT,
  `name_ja` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `summary_ja` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci DEFAULT NULL,
  `description_ja` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `deleted_at` datetime DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `uniq_name_ja` (`name_ja`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

DROP TABLE IF EXISTS `holidays`;
CREATE TABLE `holidays` (
  `difinition_id` int(11) NOT NULL,
  `date` date NOT NULL,
  `created_at` datetime NOT NULL,
  `updated_at` datetime NOT NULL,
  `deleted_at` datetime DEFAULT NULL,
  PRIMARY KEY (`date`),
  KEY `idx_difinition_id` (`difinition_id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

